import React from 'react';
import './App.css';  // Create a CSS file for styling

// Reusable PriceCard component
const PriceCard = ({ title, price, users, storage, features, buttonLabel }) => {
  return (
    <div className="price-card">
      <h2 className="title">{title}</h2>
      <h3 className="price">{price}</h3>
      <ul className="features">
        <li>{users} Users</li>
        <li>{storage} Storage</li>
        {features.map((feature, index) => (
          <li key={index} className={feature.disabled ? 'text-muted' : ''}>
            {feature.text}
          </li>
        ))}
      </ul>
      <button className="btn">{buttonLabel}</button>
    </div>
  );
};

// Main component to render the pricing cards
const App = () => {
  const pricingPlans = [
    {
      title: "FREE",
      price: "$0/month",
      users: "Single User",
      storage: "50GB",
      features: [
        { text: "Unlimited Public Projects", disabled: false },
        { text: "Community Access", disabled: false },
        { text: "Unlimited Private Projects", disabled: true },
        { text: "Dedicated Phone Support", disabled: true },
        { text: "Free Subdomain", disabled: true },
        { text: "Monthly Status Reports", disabled: true }
      ],
      buttonLabel: "BUTTON"
    },
    {
      title: "PLUS",
      price: "$9/month",
      users: "5 Users",
      storage: "50GB",
      features: [
        { text: "Unlimited Public Projects", disabled: false },
        { text: "Community Access", disabled: false },
        { text: "Unlimited Private Projects", disabled: false },
        { text: "Dedicated Phone Support", disabled: false },
        { text: "Free Subdomain", disabled: false },
        { text: "Monthly Status Reports", disabled: true }
      ],
      buttonLabel: "BUTTON"
    },
    {
      title: "PRO",
      price: "$49/month",
      users: "Unlimited Users",
      storage: "50GB",
      features: [
        { text: "Unlimited Public Projects", disabled: false },
        { text: "Community Access", disabled: false },
        { text: "Unlimited Private Projects", disabled: false },
        { text: "Dedicated Phone Support", disabled: false },
        { text: "Free Subdomain", disabled: false },
        { text: "Monthly Status Reports", disabled: false }
      ],
      buttonLabel: "BUTTON"
    }
  ];

  return (
    <div className="pricing-container">
      {pricingPlans.map((plan, index) => (
        <PriceCard
          key={index}
          title={plan.title}
          price={plan.price}
          users={plan.users}
          storage={plan.storage}
          features={plan.features}
          buttonLabel={plan.buttonLabel}
        />
      ))}
    </div>
  );
};

export default App;
